<?php

namespace IlCleme\CatalogoBundle\Entity;

use Doctrine\ORM\EntityRepository;

/**
 * FeaturevalueRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class FeaturevalueRepository extends EntityRepository
{
    public function countFeaturevalues($id_language = 4){
        $query = $this->getEntityManager()
            ->createQuery('SELECT COUNT (f.id) FROM IlClemeCatalogoBundle:Featurevalue f WHERE f.idTblLingua = ?1');
        $query->setParameters(array(
            1 => $id_language
        ));

        try {
            $count = $query->getSingleScalarResult();
            return $count;
        } catch (\Doctrine\ORM\NoResultException $e) {
            return null;
        }
    }
}
